/**
 * @param {number[]} nums
 * @param {number} target
 * @return {number[]}
 */
var twoSum = function(nums, target) {
    // Create an object to store numbers and their indices
    const numToIndex = {};

    // Iterate through the array
    for (let i = 0; i < nums.length; i++) {
        // Calculate the complement needed to reach the target
        const complement = target - nums[i];

        // Check if the complement exists in the hash map
        if (numToIndex[complement] !== undefined) {
            // If found, return the indices of the complement and the current number
            return [numToIndex[complement], i];
        }

        // If the complement is not found, store the current number and its index
        numToIndex[nums[i]] = i;
    }

    // If no solution is found (per the problem's constraints, this won't happen)
    throw new Error("No two sum solution");
};
